import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [userLogin, setUserLogin] = useState(null); // State untuk menyimpan data pengguna
  const [errorMessage, setErrorMessage] = useState(""); // State untuk menyimpan pesan error

  useEffect(() => {
    // Fetch data ketika komponen dimount
    axios
      .get("http://localhost:5000/dashboard")
      .then((response) => {
        console.log("Data session:", response.data);
        setUserLogin(response.data); // Simpan data ke state
      })
      .catch((error) => {
        const errorMsg = error.response?.data?.message || "Gagal mendapatkan data dari server.";
        console.error("Error:", errorMsg);
        setErrorMessage(errorMsg); // Simpan pesan error ke state
      });
  }, []); // [] memastikan efek hanya dijalankan sekali

  // Tampilkan loading jika data belum tersedia
  if (!userLogin && !errorMessage) {
    return <div>Loading...</div>;
  }

  // Tampilkan pesan error jika ada
  if (errorMessage) {
    return <div>Error: {errorMessage}</div>;
  }

  return (
    <div>
      <h1>Selamat Datang {userLogin.role}</h1>
      <p>Anda login sebagai <span>{userLogin.role}</span></p>
    </div>
  );
};

export default Dashboard;
