import React, { useState, useEffect } from "react";
import LoginForm from '../components/LoginForm';
import axios from 'axios';

function Home() {
    const [pengguna, setPengguna] = useState([]);

    useEffect(() => {
        getPengguna();
    }, []);

    const getPengguna = async () => {
        try {
            const response = await axios.get('http://localhost:5000/pengguna');
            setPengguna(response.data); // Simpan data pengguna ke state
            console.log(response.data); // Log untuk debugging
        } catch (error) {
            console.error("Gagal mendapatkan data pengguna:", error);
        }
    };

    return (
        <>
            <div className="container-fluid position-absolute top-50 start-50 translate-middle p-5">
                <div className="row">
                    {/* Kolom Deskripsi */}
                    <div className="col-lg-8 col-12 d-md-block d-none">
                        <h1 className="display-1 text-white fw-bolder">
                            Aplikasi Absensi
                        </h1>
                        <h2 className="display-5 text-break fw-bolder text-white">
                            Gratis .. <br />tak berlisensi untuk kebutuhan sekolah
                        </h2>
                    </div>

                    {/* Kolom Form */}
                    <div className="col-lg-4 col-12 rounded px-auto rounded-5 position-relative">
                        <LoginForm />
                    </div>
                </div>

              
                  {/* <div className="mt-5">
                      <h3 className="text-white">Daftar Pengguna:</h3>
                      <ul className="list-group">
                          {pengguna.map((user, index) => (
                              <li key={index} className="list-group-item">
                                  {user.nama} - {user.email}
                              </li>
                          ))}
                      </ul>
                  </div> */}
            </div>
        </>
    );
}

export default Home;
