import React, { useState } from "react";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Stack from '@mui/material/Stack';
import axios from 'axios';

function LoginForm(var1,var2,var3) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent form from reloading the page
    if (!username || !password) {
      setError('Username and password are required');
      return;
    }

    try {
      // Sending data to backend (for example via axios)
      const response = await axios.post('http://localhost:5000/login', { username, password });

      if (response.data.success) {
        // Handle successful login (redirect, etc.)
        alert('Login successful');
      } else {
        setError('Invalid username or password');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An error occurred, please try again');
    }
  };

  return (
    <div className='container d-sm-block'>
      <Box 
        component="form" 
        sx={{ 
          p: '2', 
          border: '1px dash mediumseagreen', 
          display: 'flex', 
          flexDirection: 'column', 
          alignItems: 'center',
          backgroundColor: '#100e1c',
          borderWidth: "1px",
          height: '400px',
        }}
        onSubmit={handleSubmit}  // Handling form submission
      >
        <Stack direction="row" alignItems="center" gap={1}>
          <AccountCircleIcon sx={{
            color: "mediumseagreen",
            fontSize: "150px",
          }} />
        </Stack>

        <TextField 
          id="username" 
          label="Username" 
          variant="outlined" 
          sx={{
            m: 1,
            "& .MuiOutlinedInput-root": {
              color: "mediumseagreen",
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "1px",
              },
            },
            "& .MuiInputLabel-outlined": {
              color: "mediumseagreen",
            },
            "&.Mui-focused": {
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "3px",
              },
            },
          }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}  // Capturing username input
        />

        <TextField 
          id="password" 
          label="Password" 
          variant="outlined" 
          type="password"  // Making sure it's a password field
          sx={{
            m: 1,
            "& .MuiOutlinedInput-root": {
              color: "mediumseagreen",
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "1px",
              },
            },
            "& .MuiInputLabel-outlined": {
              color: "mediumseagreen",
            },
            "&.Mui-focused": {
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "3px",
              },
            },
          }}
          value={password}
          onChange={(e) => setPassword(e.target.value)}  // Capturing password input
        />

        {/* Displaying error message if there is any */}
        {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}

        <Button 
          variant="outlined" 
          color="success"  
          type="submit"  // Making the button submit the form
          sx={{ mt: 2 }}
        >
          Login
        </Button>
      </Box>
    </div>
  );
}

export default LoginForm(useState);
