import React, { useState, useEffect } from "react";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Stack from '@mui/material/Stack';
import { axios } from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [pengguna, setPengguna] = useState([]);
  const [error, setError] = useState('');

  // Function to fetch pengguna data and check credentials
  const getLogin = async (username, password) => {
    try {
      const response = await axios.get('http://localhost:5000/pengguna');
      setPengguna(response.data);

      // Check if the username and password match any pengguna
      const user = response.data.find(p => p.username === username && p.password === password);
      if (user) {
        // Logic after successful login (e.g., redirect or show a success message)
        alert('Login Successful');
        // You can redirect or perform other actions here.
      } else {
        setError('Invalid username or password');
      }
    } catch (error) {
      console.error('Error fetching pengguna data:', error);
      setError('An error occurred, please try again');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      getLogin(username, password);
    } else {
      setError('Username and password are required');
    }
  };

  return (
    <div className='container d-sm-block'>
      <Box 
        component="form" 
        sx={{ 
          p:'2', 
          border: '1px dash mediumseagreen', 
          display: 'flex', 
          flexDirection: 'column', 
          alignItems: 'center',
          backgroundColor: '#100e1c',
          borderWidth: "1px",
          height:'400px',
        }} 
        onSubmit={handleSubmit}
      >
        <Stack direction="row" alignItems="center" gap={1}>
          <AccountCircleIcon sx={{
            color:"mediumseagreen",
            fontSize:"150px",
          }} />
        </Stack>

        <TextField 
          id="username" 
          label="Username" 
          variant="outlined" 
          sx={{
            m:1,
            "& .MuiOutlinedInput-root": {
              color: "mediumseagreen",
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "1px",
              },
            },
            "& .MuiInputLabel-outlined": {
              color: "mediumseagreen",
            },
            "&.Mui-focused": {
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "3px",
              },
            },
          }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <TextField 
          id="password" 
          label="Password" 
          variant="outlined" 
          type="password"
          sx={{
            m:1,
            "& .MuiOutlinedInput-root": {
              color: "mediumseagreen",
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "1px",
              },
            },
            "& .MuiInputLabel-outlined": {
              color: "mediumseagreen",
            },
            "&.Mui-focused": {
              "& .MuiOutlinedInput-notchedOutline": {
                borderColor: "mediumseagreen",
                borderWidth: "3px",
              },
            },
          }}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}

        <Button 
          variant="outlined" 
          color="success"  
          type="submit"
          sx={{ mt: 2 }}
        >
          Login
        </Button>
      </Box> 
    </div>
  );
}

export default LoginForm;
